let modal=document.getElementById("lightShowModal"),body=document.getElementById("body");function openModal(){let e=document.documentElement.style.getPropertyValue("--scroll-y");modal.style.display="block",body.style.position="fixed",body.style.top=`-${e}`,body.style.right="0",body.style.left="0",body.style.bottom="0"}function closeModal(){let e=document.body.style.top;body.style.position="",body.style.top="",body.style.right="",body.style.left="",body.style.bottom="",window.scrollTo(0,-1*parseInt(e||"0")),modal.style.display="none"}let slideIndex=1;function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){let l,t=document.getElementsByClassName("modal-slide");for(e>t.length&&(slideIndex=1),e<1&&(slideIndex=t.length),l=0;l<t.length;l++)t[l].style.display="none";t[slideIndex-1].style.display="block"}showSlides(slideIndex),window.onclick=function(e){e.target==modal&&closeModal()},window.addEventListener("scroll",()=>{document.documentElement.style.setProperty("--scroll-y",`${window.scrollY}px`)});